import React from 'react'
import { Select, Button, Table, Typography } from 'antd';
import styled from 'styled-components'
import _ from 'lodash'
import {MichiganFootballGame, FilterOptions, Ticket} from '../../../model'
import {SearchButton, Container, FilterContainer, SelectStyled, SelectContainer, EmojifiedImage} from './DashboardPage.styled'

const { Option } = Select;
const GAMES: MichiganFootballGame[] = [MichiganFootballGame.Ball, 
                        MichiganFootballGame.Arkansas, 
                        MichiganFootballGame.Wisconsin, 
                        MichiganFootballGame.PennState, 
                        MichiganFootballGame.Maryland, 
                        MichiganFootballGame.Indiana 
                    ]

const FILTER_OPTIONS: FilterOptions[] = [FilterOptions.LowestHighest, 
                        FilterOptions.HighestLowest,
                        FilterOptions.LowestSection,
                        FilterOptions.HighestSection
                    ]

const COLUMNS = [
    {
        title: 'Game',
        key: 'game',
        dataIndex: 'game'
    },
    {
        title: 'Section',
        key: 'section',
        dataIndex: 'section'
    },
    {
        title: 'Price',
        key: 'price',
        dataIndex: 'price'
    },
    {
        title: 'Buy',
        key: 'buy',
        dataIndex: 'ticketId',
        render: (text, data) => (
            <SearchButton onClick={() => {
                    console.log('ticketId: ' + data.ticketId)
                }}>
                    Purchase
            </SearchButton>
          )
    }
];

const DATA: Ticket[] = [
    {
        game: MichiganFootballGame.Ball,
        price: 25,
        id: '12345',
        section: 24
    },
    {
        game: MichiganFootballGame.Arkansas,
        price: 25,
        id: '1234567',
        section: 25
    },
    {
        game: MichiganFootballGame.Wisconsin,
        price: 25,
        id: '1234578',
        section: 26
    },
    {
        game: MichiganFootballGame.Ball,
        price: 25,
        id: '1234576',
        section: 27
    },
    {
        game: MichiganFootballGame.PennState,
        price: 25,
        id: '1234554',
        section: 20
    },
    {
        game: MichiganFootballGame.Wisconsin,
        price: 25,
        id: '1234532',
        section: 19
    },

];



const BuySection: React.FC = () => {

    const returnEmojiString = (game: MichiganFootballGame) => {
        var destination = <img src={require('../../../assets/images/football/ballstate.png')}/>
        switch (game) {
            case MichiganFootballGame.Arkansas:
                destination = <img src={require('../../../assets/images/football/arkansasstate.png')}/>
                break;
            case MichiganFootballGame.PennState:
                destination = <img src={require('../../../assets/images/football/pennstate.png')}/>
                break;
            case MichiganFootballGame.Maryland:
                destination = <img src={require('../../../assets/images/football/maryland.png')}/>
                break;
            case MichiganFootballGame.Indiana:
                destination = <img src={require('../../../assets/images/football/indiana.png')}/>
                break;
            case MichiganFootballGame.Wisconsin:
                destination = <img src={require('../../../assets/images/football/wisconsin.png')}/>
                break;

        }

        return (
            <>
                <EmojifiedImage src={require('../../../assets/images/football/michigan.png')}/><Typography.Paragraph>{' vs. '}</Typography.Paragraph>{destination}
            </>)
    }

    return (
        <Container>
            <FilterContainer>
                <SelectContainer>
                    <SelectStyled
                        style={{marginRight: 15}}
                        showSearch
                        placeholder="Select a game"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {_.map(GAMES, (game) => {
                            return (
                                <Option value={game.toLowerCase()}>{returnEmojiString(game)}</Option>
                            )
                        })}
                    </SelectStyled>
                    <SelectStyled
                        placeholder="Select a filter option"
                        optionFilterProp="children"
                    >
                        {_.map(FILTER_OPTIONS, (filter) => {
                            return (
                                <Option value={filter.toLowerCase()}>{filter}</Option>
                            )
                        })}
                    </SelectStyled>
                </SelectContainer>
                <SearchButton onClick={() => {}}>
                    {'Search üîç'}
                </SearchButton>
            </FilterContainer>
            <Table columns={COLUMNS} dataSource={DATA} pagination={{ position: ['bottomCenter'] }} />
        </Container>
    )
}

export default BuySection
