import React, { Component, createContext } from "react";
import Router, {withRouter} from "next/router";
import {message} from 'antd'
import { NextPage, NextPageContext } from 'next';
import {User, DefaultApi} from '../../api'

interface ContextProps {
  currentToken?: string,
  currentUser?: User,
  setToken: (token: string | null) => Promise<void>
}

export const UserContext = createContext<Partial<ContextProps>>({
    currentToken: null,
    currentUser: null,
    setToken: null
});
class UserProvider extends Component {
    setToken = async (token: string | null) => {
        if (token === null) {
            this.setState({currentToken: null, currentUser: null})
            return
        }
        try {
            const user = await new DefaultApi().getUser()
            this.setState({currentToken: token, currentUser: user})
        } catch (err) {
            console.error(err)
        }
    }

    state = {
        currentToken: null,
        currentUser: null,
        setToken: this.setToken
    }

    componentDidMount = async () => {
        if (this.state.currentUser !== null) {
            try {
                const body = await new DefaultApi().login({email: this.state.currentUser.email, password: this.state.currentUser.password})
                this.setToken(body.data)
            } catch (err) {
                console.error(err)
            }
        }
    }

    render() {
        return (
        <UserContext.Provider value={this.state}>
            {this.props.children}
        </UserContext.Provider>
        );
    }
}
export default UserProvider;
