import React, {ReactNode} from 'react'
import { Typography, Layout, Space, Carousel, Divider } from 'antd';
import {SmileOutlined} from '@ant-design/icons'
import styled from 'styled-components'
import {TitleYellowStyled, SubtitleYellowStyled} from './Sections.styled'
import _ from 'lodash'

const DividerStyled = styled(Divider)`
    border-top-color: #FFCB05;
`

const VerticalDivider = styled(Divider)`
    border-left-color: rgba(255,203,5, 0.3);
    height: 100%;
`

const Container = styled(Layout)`
    display: flex;
    flex-direction: column;
    height: 100%;
    padding-top: 150px;
    background-color: rgba(0,0,0,0);
`

const SectionContainer = styled(Carousel)`
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
`

const SectionAboveContainer = styled(SectionContainer)`
    display: flex;
    flex-direction: column;
    align-items: center;
`

const BoxContainer = styled.div`
    padding: 40px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`

interface BoxProps {
    title: string
    message: string
    icon: ReactNode
}

const Box: React.FC<BoxProps> = (props) => {
    const {title, message, icon} = props
    return (
        <BoxContainer>
            {icon}
            <TitleYellowStyled level={3}>{title}</TitleYellowStyled>
            <SubtitleYellowStyled level={4}>{message}</SubtitleYellowStyled>
        </BoxContainer>
    )
}

const WhyUs: React.FC = () => {
    return (
        <Container>
            <TitleYellowStyled>Why Us?</TitleYellowStyled>
            <DividerStyled/>
            <SectionAboveContainer>
                <SectionContainer>
                    <Box 
                        icon={<SmileOutlined />}
                        title={'Convenience'}
                        message={'We find buyers and sellers so you don\'t have to. Simply list a request and we\'ll find the perfect match'}
                    />
                    <VerticalDivider/>
                    <Box 
                        icon={<SmileOutlined />}
                        title={'Convenience'}
                        message={'We find buyers and sellers so you don\'t have to. Simply list a request and we\'ll find the perfect match'}
                    />
                </SectionContainer>
                <SectionContainer>
                    <Box 
                        icon={<SmileOutlined />}
                        title={'Convenience'}
                        message={'We find buyers and sellers so you don\'t have to. Simply list a request and we\'ll find the perfect match'}
                    />
                    <VerticalDivider/>
                    <Box 
                        icon={<SmileOutlined />}
                        title={'Convenience'}
                        message={'We find buyers and sellers so you don\'t have to. Simply list a request and we\'ll find the perfect match'}
                    />
                </SectionContainer>
            </SectionAboveContainer>
        </Container>
    )
}

export default WhyUs
