import React from 'react'
import { Select, Button, Table, Typography } from 'antd';
import styled from 'styled-components'
import _ from 'lodash'
import {MichiganFootballGame, FilterOptions, Ticket} from '../../../model'
import {
    GenButton, 
    Container, 
    FilterContainer, 
    SelectStyled, 
    SelectContainer, 
    returnEmojiString, 
    GenIconButton
    } from './DashboardPage.styled'
import {SearchOutlined, DollarOutlined} from '@ant-design/icons'

const { Option } = Select;
const GAMES: MichiganFootballGame[] = [MichiganFootballGame.Ball, 
                        MichiganFootballGame.Arkansas, 
                        MichiganFootballGame.Wisconsin, 
                        MichiganFootballGame.PennState, 
                        MichiganFootballGame.Maryland, 
                        MichiganFootballGame.Indiana 
                    ]

const FILTER_OPTIONS: FilterOptions[] = [FilterOptions.LowestHighest, 
                        FilterOptions.HighestLowest,
                        FilterOptions.LowestSection,
                        FilterOptions.HighestSection
                    ]

const COLUMNS = [
    {
        title: 'Game',
        key: 'game',
        dataIndex: 'game',
        render: (text, data) => returnEmojiString(data.game)
    },
    {
        title: 'Section',
        key: 'section',
        dataIndex: 'section'
    },
    {
        title: 'Price ($)',
        key: 'price',
        dataIndex: 'price'
    },
    {
        title: '',
        key: 'buy',
        dataIndex: 'ticketId',
        render: (text, data) => (
                <GenIconButton icon={<DollarOutlined style={{fontSize: 16}}/>} onClick={() => {}}>
                    Purchase
                </GenIconButton>
          )
    }
];

const DATA: Ticket[] = [
    {
        game: MichiganFootballGame.Ball,
        price: 25,
        id: '12345',
        section: 24
    },
    {
        game: MichiganFootballGame.Arkansas,
        price: 25,
        id: '1234567',
        section: 25
    },
    {
        game: MichiganFootballGame.Wisconsin,
        price: 25,
        id: '1234578',
        section: 26
    },
    {
        game: MichiganFootballGame.Ball,
        price: 25,
        id: '1234576',
        section: 27
    },
    {
        game: MichiganFootballGame.PennState,
        price: 25,
        id: '1234554',
        section: 20
    },
    {
        game: MichiganFootballGame.Wisconsin,
        price: 25,
        id: '1234532',
        section: 19
    },

];



const BuySection: React.FC = () => {

    return (
        <Container>
            <FilterContainer>
                <SelectContainer>
                    <SelectStyled
                        style={{marginRight: 15}}
                        showSearch
                        placeholder="Select a game"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {_.map(GAMES, (game) => {
                            return (
                                <Option value={game.toLowerCase()}>{game}</Option>
                            )
                        })}
                    </SelectStyled>
                    <SelectStyled
                        placeholder="Select a filter option"
                        optionFilterProp="children"
                    >
                        {_.map(FILTER_OPTIONS, (filter) => {
                            return (
                                <Option value={filter.toLowerCase()}>{filter}</Option>
                            )
                        })}
                    </SelectStyled>
                </SelectContainer>
                <GenButton style={{
                        fontFamily: 'Mark Pro', 
                        paddingBottom: 18,
                        paddingTop: 18,
                        paddingLeft: 35,
                        paddingRight: 35,
                        fontSize: 14
                    }} onClick={() => {}} icon={<SearchOutlined />}>
                    Search
                </GenButton>
            </FilterContainer>
            <Table columns={COLUMNS} dataSource={DATA} pagination={{ position: ['bottomCenter'] }} />
        </Container>
    )
}

export default BuySection
