import React from 'react'
import { Typography, Layout, Button, Carousel, Divider } from 'antd';
import styled from 'styled-components'
import {TitleStyled, SubtitleStyled as Subtitle} from '../Sections.styled/Universal'
import _ from 'lodash'

const DividerStyled = styled(Divider)`
    border-top-color: rgba(0,39,76, 0.75);
`

const Container = styled(Layout)`
    display: flex;
    flex-direction: column;
    height: 100%;
    padding-top: 10%;
    background-color: rgba(0,0,0,0);
`

const MiniContainer = styled.div`
    display: flex;
    flex-direction: column;
    padding-left: 40px;
    padding-right: 40px;
`

const SubtitleStyled = styled(Subtitle)`
    text-align: left;
`

const CarouselContainer = styled(Carousel)`
    display: flex;
    justify-content: center;
    align-items: center;
    color: rgb(0,39,76);
`

const CardContainer = styled.div`
    padding: 30px;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const BUYING_DATA: {text: string}[] = [
                    {text: 'Search for a ticket on the buying page'}, 
                    {text: 'Choose and purchase your desired ticket'}, 
                    {text: 'Your seller will e-transfer you the ticket within 2 days via Michigan\'s transfer website'}, 
                    {text: 'Confirmed you\'ve received the ticket'}, 
                    {text: 'Watch Michigan Win!'}
                ]

const HowItWorks: React.FC = () => {
    return (
        <Container>
            <TitleStyled level={2}>How It Works</TitleStyled>
            <DividerStyled/>
            <MiniContainer>
                <SubtitleStyled level={4}>Buy</SubtitleStyled>
                <CarouselContainer>
                    {_.map(BUYING_DATA, ({text}) => {
                        return (
                            <CardContainer>
                                <SubtitleStyled style={{fontFamily: 'Mark Pro', textAlign: 'center'}} level={4}>{text}</SubtitleStyled>
                            </CardContainer>
                        )
                    })}
                </CarouselContainer>
            </MiniContainer>
        </Container>
    )
}

export default HowItWorks
