import React, { useEffect, ReactNode, CSSProperties } from 'react';
import { Card, Typography, Button } from 'antd';
import Navbar from '../Universal/Navbar'
import styled from 'styled-components'
import BackgroundPolygons from '../../components/BackgroundPolygons';
import Footer from '../Universal/Footer'
import Page from '../../components/Page'
import {LayoutNoBG, FULL_SCREEN, FULL_SCREEN_WITH_BAR} from '../Universal/PageUniversal'
import OpenPage from '../Universal/OpenPage'

const VerifyEmailComponent: React.FC = () => {
    return (
        <LayoutNoBG>
            <Navbar selected={0} isLoggedIn={false}/>
            <BackgroundPolygons/>
            <Page style={FULL_SCREEN_WITH_BAR}>
                <Card>
                    <Typography.Title>We know email confirmations suck, but we do it for you -</Typography.Title>
                    <Typography.Title>to make sure everyone on the platform is a student. Please check your email for a confirmation link.</Typography.Title>
                    <Button onClick={() => OpenPage('/')}>Go Home</Button>
                </Card>
            </Page>
            <Footer/>
        </LayoutNoBG>
    )
};

export default VerifyEmailComponent
