import React, {useContext, useEffect, CSSProperties} from 'react'
import Head from 'next/head'
import MainPage from '../src/areas/MainPage'
import LoginCard from '../src/areas/LoginCard'
import styled from 'styled-components'
import LoginBackground from '../src/components/LoginBackground'
import OpenPage from '../src/areas/OpenPage'
import {UserContext} from '../src/lib/UserContext'

const Index: React.FC = () => {
    const { api, setToken } = useContext(UserContext)

    useEffect(() => {
        const email = localStorage.getItem('email')
        const password = localStorage.getItem('password')
        const seeIfLogin = async () => {
            if (email !== null && email !== '') {
                try {
                    const body = await api.login({email: email, password: password})
                    setToken(body.data)
                    OpenPage('/dashboard')
                } catch (err) {
                    console.error(err)
                }
            }
        }

        seeIfLogin()
    }, [])

    return (
        <>
            <Head>
                <title>MasterSeats â€¢ Home</title>
            </Head>
            <MainPage/>
        </>
    );
};

export default Index;
