import React from 'react'
import { Select, Button } from 'antd';
import styled from 'styled-components'
import _ from 'lodash'

const { Option } = Select;
const GAMES: string[] = ["Michigan vs. Ball State", 
                        "Michigan vs. Arkansas State", 
                        "Michigan vs. Wisconsin",
                        "Michigan vs. Penn State",
                        "Michigan vs. Maryland",
                        "Michigan vs. Indiana"]

const FILTER_OPTIONS: string[] = ["Price - lowest to highest", 
                                "Price - highest to lowest", 
                                "Lowest Section",
                                "Highest Section"]

const Container = styled.div`
    padding-left: 5%;
    padding-right: 5%;
    padding-top: 15px;
    padding-bottom: 30px;
`

const FilterContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
`

const SelectStyled = styled(Select)`
    text-align: left;
    width: 48%;
`

const SearchButton = styled(Button)`
    color: #00274c;
    border: 1px solid #00274c;
    padding-bottom: 4px;
    padding-top: 4px;
`

const SelectContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    width: 100%;
`

const BuySection: React.FC = () => {
    return (
        <Container>
            <FilterContainer>
                <SelectContainer>
                    <SelectStyled
                        style={{marginRight: 15}}
                        showSearch
                        placeholder="Select a game"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {_.map(GAMES, (game) => {
                            return (
                                <Option value={game.toLowerCase()}>{game}</Option>
                            )
                        })}
                    </SelectStyled>
                    <SelectStyled
                        placeholder="Select a filter option"
                        optionFilterProp="children"
                    >
                        {_.map(FILTER_OPTIONS, (filter) => {
                            return (
                                <Option value={filter.toLowerCase()}>{filter}</Option>
                            )
                        })}
                    </SelectStyled>
                </SelectContainer>
                <SearchButton>
                    {'Search 🔍'}
                </SearchButton>
            </FilterContainer>
        </Container>
    )
}

export default BuySection
