import React, { useEffect, useRef, useState } from 'react'
import { Menu, Layout } from 'antd';
import styled from 'styled-components'

const { Header } = Layout;

const StyledHeader = styled(Header)`
    display: flex;
    padding: 12px;
    padding-left: 30px;
    padding-right: 30px;
    height: 90px;
    width: 100%;
    position: sticky;
    z-index: 5;
    top: 0;
    transition: 0.3s;
`;

const MenuLogoContainer = styled.div`
    display: flex;
    flex-direction: row;
    flex: 1;
    justify-content: space-between;
    width: 100%;
    align-items: center;
`;

const MenuStyled = styled(Menu)`
    display: flex;
    flex-direction: row;
    flex: 1;
    justify-content: flex-end;
`

const NavBarItemMain = styled(Menu.Item)`
    font-family: 'Mark Pro Bold';
    background-color: #00274c !important;
    color: #FFCB05;
    border-radius: 15px;
    transition: 0.3s;
    &:hover {
        opacity: 0.7;
    }
`
const NavBarItem = styled(NavBarItemMain)`
    background-color: rgba(0,0,0,0) !important;
    color: #00274c;
    font-family: 'Mark Pro Medium';
`

const Navbar: React.FC = () => {
    const [isAtTop, setIsAtTop] = useState(true);

    useEffect(() => {
      const handleScroll = () => {
        const currentScrollY = window.scrollY;
        console.log('scroll' + currentScrollY)
        if (currentScrollY <= 0) {
            setIsAtTop(false);
        }
        if (currentScrollY > 0) {
            setIsAtTop(true);
        }
      };

      window.addEventListener("scroll", handleScroll, { passive: true });

      return () => window.removeEventListener("scroll", handleScroll);
    }, [isAtTop]);

    return (
        <StyledHeader style={{backgroundColor: isAtTop ? '#FFCB05' : 'rgba(255,255,255,0.85)'}}>
            <MenuLogoContainer>
                <a href={'/'}>
                    <img height={42} src={require('../../assets/images/logo.png')} alt={"MasterSeats Logo"}/>
                </a>
                <MenuStyled>
                    <NavBarItemMain>
                        Home
                    </NavBarItemMain>
                    <div style={{width: 20}}/>
                    <NavBarItem>
                        Login
                    </NavBarItem>
                    <div style={{width: 5}}/>
                    <NavBarItem>
                        Sign Up
                    </NavBarItem>
                </MenuStyled>
            </MenuLogoContainer>
        </StyledHeader>
    )
}

export default Navbar
