import React from 'react'
import { Select, Button, Table } from 'antd';
import styled from 'styled-components'
import _ from 'lodash'
import {MichiganFootballGame, FilterOptions, Ticket} from '../../../model'

const { Option } = Select;
const GAMES: MichiganFootballGame[] = [MichiganFootballGame.Ball, 
                        MichiganFootballGame.Arkansas, 
                        MichiganFootballGame.Wisconsin, 
                        MichiganFootballGame.PennState, 
                        MichiganFootballGame.Maryland, 
                        MichiganFootballGame.Indiana 
                    ]

const FILTER_OPTIONS: FilterOptions[] = [FilterOptions.LowestHighest, 
                        FilterOptions.HighestLowest,
                        FilterOptions.LowestSection,
                        FilterOptions.HighestSection
                    ]

const COLUMNS = [
    {
        title: 'Game',
        key: 'game',
        dataIndex: 'game'
    },
    {
        title: 'Section',
        key: 'section',
        dataIndex: 'section'
    },
    {
        title: 'Price',
        key: 'price',
        dataIndex: 'price'
    },
    {
        title: 'Buy',
        key: 'buy',
        dataIndex: 'ticketId',
        render: (text, data) => (
            <SearchButton onClick={() => {
                    console.log('ticketId: ' + data.ticketId)
                }}>
                    Purchase
            </SearchButton>
          )
    }
];

const DATA: Ticket[] = [
    {
        game: MichiganFootballGame.Ball,
        price: 25,
        id: '12345',
        section: 24
    },
    {
        game: MichiganFootballGame.Arkansas,
        price: 25,
        id: '1234567',
        section: 25
    },
    {
        game: MichiganFootballGame.Wisconsin,
        price: 25,
        id: '1234578',
        section: 26
    },
    {
        game: MichiganFootballGame.Ball,
        price: 25,
        id: '1234576',
        section: 27
    },
    {
        game: MichiganFootballGame.PennState,
        price: 25,
        id: '1234554',
        section: 20
    },
    {
        game: MichiganFootballGame.Wisconsin,
        price: 25,
        id: '1234532',
        section: 19
    },

];

const Container = styled.div`
    padding-left: 5%;
    padding-right: 5%;
    padding-top: 15px;
    padding-bottom: 30px;
`

const FilterContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;
`

const SelectStyled = styled(Select)`
    text-align: left;
    width: 40%;
`

const SearchButton = styled(Button)`
    color: #00274c;
    border: 1px solid #00274c;
    padding-bottom: 4px;
    padding-top: 4px;
`

const SelectContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    width: 100%;
`

const TableStyled = styled(Table)`

`

const BuySection: React.FC = () => {
    return (
        <Container>
            <FilterContainer>
                <SelectContainer>
                    <SelectStyled
                        style={{marginRight: 15}}
                        showSearch
                        placeholder="Select a game"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {_.map(GAMES, (game) => {
                            return (
                                <Option value={game.toLowerCase()}>{game}</Option>
                            )
                        })}
                    </SelectStyled>
                    <SelectStyled
                        placeholder="Select a filter option"
                        optionFilterProp="children"
                    >
                        {_.map(FILTER_OPTIONS, (filter) => {
                            return (
                                <Option value={filter.toLowerCase()}>{filter}</Option>
                            )
                        })}
                    </SelectStyled>
                </SelectContainer>
                <SearchButton>
                    {'Search 🔍'}
                </SearchButton>
            </FilterContainer>
            <TableStyled columns={COLUMNS} dataSource={DATA} />
        </Container>
    )
}

export default BuySection
