import React, {useState, useEffect, useContext} from 'react'
import {Table, Modal, Grid, message} from 'antd'
import {Container, 
        returnEmojiString, 
        GenIconButton, 
        CloseAdjustedOutline,
        ExclamationCircleAdjustedOutlined} from '../../DashboardPage/DashboardPage.styled'
import {MichiganFootballGame, Ticket, TicketStatus, PostTicketBody} from '../../../../api'
import {ScreenSize} from '../../../lib'
import AddTicketModal from '../../DashboardPage/AddTicketModal'
import {UserContext} from '../../../lib/UserContext'
import OpenPage from '../../Universal/OpenPage'

const {useBreakpoint} = Grid

interface PendingProps {
    pendingSold: Ticket[],
    pendingBuy: Ticket[],
    confirmTransferSeller: (ticket_id: string) => void,
    confirmTransferBuyer: (ticket_id: string) => void
    refundTicket: (ticket_id: string) => void
}

const Pending: React.FC<PendingProps> = props => {
    const {pendingSold, pendingBuy, confirmTransferSeller, confirmTransferBuyer, refundTicket} = props

    const screens = useBreakpoint()
    const [screenSize, setScreenSize] = useState<ScreenSize>(undefined)

    const columns = (isBuyer: boolean) => {
        var refund_col = null
        if (isBuyer) {
            return [
                {
                    title: 'Game',
                    key: 'game',
                    dataIndex: 'game',
                    render: (text, data) => returnEmojiString(data.game)
                },
                {
                    title: 'Section',
                    key: 'section',
                    dataIndex: 'section'
                },
                {
                    title: 'Price ($)',
                    key: 'price',
                    dataIndex: 'price'
                },
                {
                    title: '',
                    key: 'confirm',
                    dataIndex: 'id',
                    render: (text, data) => {
                        var disabled: boolean
                        if (isBuyer) {
                            disabled = !data.confirmed_seller_transfer
                        } else {
                            disabled = !data.confirmed_buyer_transfer
                        }

                        return (<div style={{display: 'flex', justifyContent: 'flex-end'}}>
                            <GenIconButton disabled={disabled} icon={<ExclamationCircleAdjustedOutlined />} onClick={async () => {
                                await confirmTransferBuyer(data.id)
                            }}>
                                {screenSize > 0 ? "Confirm Transfer" : null}
                            </GenIconButton>
                        </div>)
                    }
                },
                {
                    title: '',
                    key: 'refund',
                    dataIndex: 'id',
                    render: (text, data) => {
                        var disabled: boolean
                        if (isBuyer) {
                            disabled = !data.confirmed_seller_transfer
                        } else {
                            disabled = !data.confirmed_buyer_transfer
                        }

                        return (<div style={{display: 'flex', justifyContent: 'flex-end'}}>
                            <GenIconButton disabled={disabled} icon={<CloseAdjustedOutline />} onClick={async () => {
                                await refundTicket(data.id)
                            }}>
                                {screenSize > 0 ? "Get Refund" : null}
                            </GenIconButton>
                        </div>)
                    }
                }
            ]
        } else {
            return [
                {
                    title: 'Game',
                    key: 'game',
                    dataIndex: 'game',
                    render: (text, data) => returnEmojiString(data.game)
                },
                {
                    title: 'Section',
                    key: 'section',
                    dataIndex: 'section'
                },
                {
                    title: 'Price ($)',
                    key: 'price',
                    dataIndex: 'price'
                },
                {
                    title: '',
                    key: 'confirm',
                    dataIndex: 'id',
                    render: (text, data) => {
                        var disabled: boolean
                        if (isBuyer) {
                            disabled = !data.confirmed_seller_transfer
                        } else {
                            disabled = !data.confirmed_buyer_transfer
                        }

                        return (<div style={{display: 'flex', justifyContent: 'flex-end'}}>
                            <GenIconButton disabled={disabled} icon={<ExclamationCircleAdjustedOutlined />} onClick={async () => {
                                await confirmTransferSeller(data.id)
                            }}>
                                {screenSize > 0 ? "Confirm Transfer" : null}
                            </GenIconButton>
                        </div>)
                    }
                },
                {
                    title: '',
                    key: 'refund',
                    dataIndex: 'id',
                    render: (text, data) => {
                        var disabled: boolean
                        if (isBuyer) {
                            disabled = !data.confirmed_seller_transfer
                        } else {
                            disabled = !data.confirmed_buyer_transfer
                        }

                        return (<div style={{display: 'flex', justifyContent: 'flex-end'}}>
                            <GenIconButton icon={<CloseAdjustedOutline />} onClick={async () => {
                                await refundTicket(data.id)
                            }}>
                                {screenSize > 0 ? "Get Refund" : null}
                            </GenIconButton>
                        </div>)
                    }
                }
            ]
        }
    }

    useEffect(() => {
        Object.entries(screens)
        .filter(screen => !!screen[1])
        .map(screen => {
            if (screen[0] !== ScreenSize[screenSize]) {
                setScreenSize(ScreenSize[screen[0]])
            }
        })
    }, [screens])

    return (
        <Container>
            <Table columns={columns(false)} dataSource={pendingSold} pagination={{ position: ['bottomCenter'] }} />
            <Table columns={columns(true)} dataSource={pendingBuy} pagination={{ position: ['bottomCenter'] }} />
        </Container>
    )
}

export default Pending
