import React, {CSSProperties, ReactNode, useState, useEffect} from 'react'
import {Row, Col, Grid} from 'antd'
import {ScreenSize} from '../../model'
import styled from 'styled-components'
import media from 'styled-media-query'
const {useBreakpoint} = Grid

const RowStyled = styled(Row)`
    ${media.lessThan("small")`
        padding-left: 35px;
        padding-right: 35px;
    `}
`

interface PageProps {
    style: CSSProperties
    children?: ReactNode
}

const Page: React.FC<PageProps> = (props) => {
    const screens = useBreakpoint()
    const [screenSize, setScreenSize] = useState<ScreenSize>(undefined)

    useEffect(() => {
        Object.entries(screens)
        .filter(screen => !!screen[1])
        .map(screen => {
            if (screen[0] !== ScreenSize[screenSize]) {
                setScreenSize(ScreenSize[screen[0]])
            }
        })
    }, [screens])
    return (
        <RowStyled style={props.style}>
            {screenSize <= 1 ? 
                <Col style={props.style} span={24}>
                    {props.children}
                </Col>
            : 
            <>
                <Col span={4}/>
                <Col style={props.style} span={16}>
                    {props.children}
                </Col>
                <Col span={4}/>
            </>
            }
        </RowStyled>
    )
}

export default Page
